import { useState, useEffect } from 'react'
import NFLPlayers from './components/NFLPlayers'
import SearchBox from './components/SearchBox'

const App = () => {
  const [nflPlayers, setNflPlayers] = useState([])
  const [searchTerm, setSearchTerm] = useState([])

  const sortTypes = {
    YdsUp: {
      class: 'yds-up',
      fn: (a, b) => a.Yds - b.Yds
    },
    YdsDown: {
      class: 'yds-down',
      fn: (a, b) => b.Yds - a.Yds
    },
    LngUp: {
      class: 'lng-up',
      fn: (a, b) => a.Lng - b.Lng
    },
    LngDown: {
      class: 'lng-down',

    }
  }

  useEffect(() => {
    const getNflPlayers = async () => {
      const nflPlayersFromServer = await fetchNflPlayers()
      setNflPlayers(nflPlayersFromServer)
    }

    getNflPlayers() 
  }, [])

  // Fetch Tasks
  const fetchNflPlayers = async () => {
    const res = await fetch('http://localhost:5000/nflPlayers')
    const data = await res.json()
    return data 
  }

  return (
    <div className='container'>
      <h1>NFL Player Statistics</h1>
      <SearchBox placeholder="Search Players..." handleChange={(e) => setSearchTerm(e.target.value)} />
      {nflPlayers.length > 0 ? (
        <NFLPlayers nflPlayers={
          nflPlayers.filter((val) => {
            if ( searchTerm == "" ) {
              return val
            } else if ( val.Player.toLowerCase().includes(searchTerm.toLowerCase()) ) {
              return val
            }
          })
        }/>
      ) : ( 'No NFL Players' )}
    </div>
  )
}

export default App
