import { useState, useEffect } from 'react'
import NFLPlayers from './components/NFLPlayers'

const App = () => {
  const [nflPlayers, setNflPlayers, searchTerm, setSearchTerm] = useState([])

  useEffect(() => {
    const getNflPlayers = async () => {
      const nflPlayersFromServer = await fetchNflPlayers()
      setNflPlayers(nflPlayersFromServer)
    }

    getNflPlayers() 
  }, [])

  // Fetch Tasks
  const fetchNflPlayers = async () => {
    const res = await fetch('http://localhost:5000/nflPlayers')
    const data = await res.json()
    return data 
  }

  return (
    <div className='container'>
      <input type="text" placeholder="Search Player..." onChange={(event) => {
        setSearchTerm(event.target.value);
      }}
      />
      {nflPlayers.length > 0 ? (
        <NFLPlayers nflPlayers={nflPlayers} />
      ) : ( 'No NFL Players' )}
    </div>
  )
}

export default App
