import NFLPlayer from './NFLPlayer'

const NFLPlayerTableBody = ({ nflPlayers }) => {

  const searchTerm = "";
  const setSearchTerm = function(val) {
    searchTerm = val;
  }

  return (
    <div>
      <input 
        type = "text" 
        placeholder="Search Player..."
        onChange={(event) => {
          setSearchTerm(event.target.value)
        }}
      />
      <tbody>
        {nflPlayers.filter((val) => {
          if ( searchTerm == "" ) {
            return val;
          } else if ( val.Player.toLowerCase().includes(searchTerm.toLowerCase()) ) {
            return val;
          }
        }).map((nflPlayer, index) => (
          <NFLPlayer key={index} nflPlayer={nflPlayer} />
        ))}
      </tbody>
    </div>
  )
}

export default NFLPlayerTableBody
