import NFLPlayer from './NFLPlayer'

state = {
  currentSort: 'default'
}

onSortChange = (key) => {
  const { currentSort } = this.state;
		let nextSort;

		if (currentSort === 'YdsDown') nextSort = 'YdsUp';
    else if (currentSort === 'LngDown') nextSort = 'LngUp';
    else if (currentSort === 'TdDown') nextSort = 'TdUp';
		else if (currentSort === 'default') nextSort = 'down';

		this.setState({
			currentSort: nextSort
		});
}

const sortTypes = {
  YdsUp: {
    class: 'yds-up',
    fn: (a, b) => a.Yds - b.Yds
  },
  YdsDown: {
    class: 'yds-down',
    fn: (a, b) => b.Yds - a.Yds
  },
  LngUp: {
    class: 'lng-up',
    fn: (a, b) => a.Lng - b.Lng
  },
  LngDown: {
    class: 'lng-down',
    fn: (a, b) => b.Lng - a.Lng
  },
  TDUp: {
    class: 'td-up',
    fn: (a, b) => a.TD - b.TD
  },
  TDDown: {
    class: 'td-down',
    fn: (a, b) => b.TD - a.TD
  },
  default: {
    class: 'sort',
    fn: (a,b) => a
  }
}

const NFLPlayerTableBody = ({ nflPlayers }) => {
  const {currentSort} = this.state;

  return (
    <div>
      <thead>
        <tr>
          {
            Object.keys(nflPlayers[0]).map((key) => {
              return (
                <th key={key} onClick={
                  this.onSortChange(key)
                  }>
                  <h3>{key}</h3>
                </th>
              )
            })
          }
        </tr>
      </thead>
      <tbody>
        {nflPlayers.sort(sortTypes[currentSort].fn).map((nflPlayer, index) => (
          <NFLPlayer key={index} nflPlayer={nflPlayer} />
        ))}
      </tbody>
    </div>
  )
}

export default NFLPlayerTableBody
