import { useState, useEffect } from 'react'
import NFLPlayers from './components/NFLPlayers'
import SearchBox from './components/SearchBox'

const App = () => {
  const [nflPlayers, setNflPlayers] = useState('')
  const [searchTerm, setSearchTerm] = useState('')

  useEffect(() => {
    const getNflPlayers = async () => {
      const nflPlayersFromServer = await fetchNflPlayers()
      setNflPlayers(nflPlayersFromServer)
    }

    getNflPlayers() 
  }, [])

  // Fetch Tasks
  const fetchNflPlayers = async () => {
    const res = await fetch('http://localhost:5000/nflPlayers')
    const data = await res.json()
    return data 
  }

  function exportCsv(nflPlayers) {
    console.log(nflPlayers);
    let csvContent = "data:text/csv;charset=utf-8,";
    let nflPlayer = nflPlayers.join(",");
    csvContent += nflPlayer + "\r\n";
    var encodedUri = encodeURI(csvContent);
    var link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "nfl_players.csv");
    document.body.appendChild(link);
    link.click();
  }

  return (
    <div className='container'>
      <h1>NFL Player Statistics</h1>
      <SearchBox placeholder="Search Players..." handleChange={(e) => setSearchTerm(e.target.value)} />
      <button onClick= {() => exportCsv(nflPlayers)}>Export CSV</button>
      {nflPlayers.length > 0 ? (
        <NFLPlayers nflPlayers={
          nflPlayers.filter((val) => {
            var value
            if ( searchTerm === "" ) {
              value = val
            } else if ( val.Player.toLowerCase().includes(searchTerm.toString().toLowerCase()) ) {
              value =  val
            }
            return value
          })
        }/>
      ):<tr> <td> - </td></tr>}
    </div>
  )
}

export default App
